$(document).ready(function () {
    $("body").append("<a class='top_button' title='Back To Top' href=''>TOP</a>");

    $(function () {
        $(window).scroll(function () {
            if ($(this).scrollTop() > 70) {
                $('.top_button').fadeIn();
            } else {
                $('.top_button').fadeOut();
            }
        });
        // scroll body to 0px on click
        $('.top_button').click(function () {
            $('body,html').animate({
                scrollTop: 0
            }, 800);
            return false;
        });

        $(window).scroll(function () {
            if ($(this).scrollTop() > 160) {
                jQuery("nav.nav-container").addClass("nav-container-fixed");
                jQuery("#header-bottom").addClass('container-with-nav-fixed');
            } else {
                jQuery("nav.nav-container").removeClass("nav-container-fixed");
                jQuery("#header-bottom").removeClass('container-with-nav-fixed');
            }
        });
    });
});
